import Thumbnail from './Thumbnail';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';

import { useState } from 'react';
const logo = require('../logo.png');

export default function Search() {


    //const api = 'http://182.176.84.112:5000/';
    const api = 'http://localhost:5000/';
    const [img, setImg] = useState("https://i.ytimg.com/vi/9bZkp7q19f0/maxresdefault.jpg");
    const [downloadFound, setDownloadFound] = useState(false);
    const [searchURL, setSearchURL] = useState("");
    const [loading, setLoading] = useState(false);
    const [title, setTitle] = useState('PLACEHOLDER');
    const [downloadProgress, setDownloadProgress] = useState(0);
    // const [isDownloading, setIsDownloading] = useState(false);
    const [downloadLink, setDownloadLink] = useState("NULL");
    const [linkAvailable, setLinkAvailable] = useState(false);
    const [globalURL, setGlobalURL] = useState('NONE');
    ////////////////////////////////////// TOAST Functions //////////////////////////////////////
    const toastErrorInternal = () => {
        toast.error('Error -  INTERNAL', {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: false,
            draggable: true,
            progress: undefined,
            theme: "colored",
        });

    }
    const toastErrorURL = () => {
        toast.error('Error -  Fix URL', {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: false,
            draggable: true,
            progress: undefined,
            theme: "colored",
        });

    }

    const toastSuccess = () => {
        toast.success('Success !', {
            position: "top-right",
            autoClose: 2000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: false,
            draggable: true,
            progress: undefined,
            theme: "colored",
        });

    }
    ////////////////////////////////////// TOAST Functions //////////////////////////////////////

    const handleSearch = async (e) => {

    }

    return (
        <div>
            <div class="grid grid-cols-2">
                <div className="col-span-1 mx-auto pl-2 border-2">
                    <Thumbnail src={logo} title="Test" alt="PlaceHolder" />
                </div>
                <div className="col-span-1 mx-auto w-full px-3 border-2">
                    <input value={searchURL} onChange={e => setSearchURL(e.target.value)} onClick={() => { setSearchURL("") }}
                        class={inputClass}
                        id="search" type="text" placeholder="Enter View URL"></input>
                    <button onClick={(e) => handleSearch(e)}
                        className={inputButtonClass}>Search</button>
                </div>


            </div>
        </div>
    );
};

const inputClass = "shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline";
const inputButtonClass = "bg-blue-500 hover:bg-blue-700 text-white font-bold my-2 py-2 px-4 rounded";
